    private void btntachActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btntachActionPerformed
        // TODO add your handling code here:
        String hoten = txthoten.getText().trim();
        int i = hoten.lastIndexOf(" ");
        String ho = hoten.split(" ")[0];
       // String ho = "";
        String ten = "";
        if(i > 0){
            //ho = hoten.substring(0,i).trim();
            ten = hoten.substring(i).trim();
        }
        else
        {
            ho = hoten;
        }
        txtho.setText(ho);
        txtten.setText(ten);
    }//GEN-LAST:event_btntachActionPerformed

